import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);

  useEffect(() => {
    axios.get('http://localhost:5000/api/questions')
      .then(response => {
        setQuestions(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the questions!', error);
      });
  }, []);

  if (questions.length === 0) {
    return <div>Loading...</div>;
  }

  const handleAnswerClick = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      alert(`Quiz finished! Your score is ${score} / ${questions.length}`);
    }
  };

  const progress = (currentQuestion / questions.length) * 100;

  return (
    <div className="quiz-container">
      <h2>Question {currentQuestion + 1} of {questions.length}</h2>
      <p>{decodeURIComponent(questions[currentQuestion].question)}</p>

      <div className="options">
        {[
          ...questions[currentQuestion].incorrect_answers.map(ans => ({ text: decodeURIComponent(ans), isCorrect: false })),
          { text: decodeURIComponent(questions[currentQuestion].correct_answer), isCorrect: true }
        ].sort(() => Math.random() - 0.5).map((option, index) => (
          <button key={index} onClick={() => handleAnswerClick(option.isCorrect)}>
            {option.text}
          </button>
        ))}
      </div>

      <div className="progress-container">
        <div className="progress-bar" style={{ width: `${progress}%` }}></div>
      </div>

      <div className="score-container">
        <p>Score: {score} / {questions.length}</p>
        <p>Max Score: {(score / (currentQuestion + 1) * 100).toFixed(2)}%</p>
      </div>
    </div>
  );
}

export default App;
