import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [answered, setAnswered] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/questions')
      .then(response => {
        setQuestions(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the questions!', error);
      });
  }, []);

  if (questions.length === 0) {
    return <div>Loading...</div>;
  }

  const handleAnswerClick = (isCorrect) => {
    setAnswered(true);
    setIsCorrect(isCorrect);
    if (isCorrect) {
      setScore(score + 1);
    }
  };

  const handleNextQuestion = () => {
    setAnswered(false);
    setIsCorrect(null);
    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      alert(`Quiz finished! Your score is ${score} / ${questions.length}`);
    }
  };

  const progress = (currentQuestion / questions.length) * 100;
  const maxScoreProgress = (score / questions.length) * 100;
  const currentScorePercentage = (score / (currentQuestion + 1)) * 100;

  return (
    <div className="quiz-container">
      <div className="progress-container">
        <div className="progress-bar" style={{ width: `${progress}%` }}></div>
      </div>

      <h2>Question {currentQuestion + 1} of {questions.length}</h2>
      <p>{decodeURIComponent(questions[currentQuestion].question)}</p>

      <div className="options">
        {[
          ...questions[currentQuestion].incorrect_answers.map(ans => ({ text: decodeURIComponent(ans), isCorrect: false })),
          { text: decodeURIComponent(questions[currentQuestion].correct_answer), isCorrect: true }
        ].sort(() => Math.random() - 0.5).map((option, index) => (
          <button 
            key={index} 
            onClick={() => handleAnswerClick(option.isCorrect)} 
            disabled={answered}
            style={answered && option.isCorrect ? { backgroundColor: 'green' } : {}}
          >
            {option.text}
          </button>
        ))}
      </div>

      {answered && (
        <>
          <div className="answer-feedback">
            {isCorrect ? 'Correct!' : 'Sorry!'}
          </div>
          <button onClick={handleNextQuestion}>Next Question</button>
        </>
      )}

      <div className="score-container">
        <div className="score-progress-bar" style={{ width: `${currentScorePercentage}%` }}></div>
        <div className="score-text">
          <p>Score: {score} / {questions.length}</p>
          <p>Max Score: {maxScoreProgress.toFixed(2)}%</p>
        </div>
      </div>
    </div>
  );
}

export default App;
